package completions

import (
	"fmt"
	"strings"
)

// GenerateFish generates a fish completion script
func GenerateFish(commands []CommandInfo) string {
	var b strings.Builder

	b.WriteString(`# fp fish completion script
# Generated by fp setup

# Disable file completions for fp
complete -c fp -f

# Global flags
complete -c fp -s h -l help -d 'Show help'
complete -c fp -s v -l version -d 'Show version'
complete -c fp -l no-color -d 'Disable colored output'
complete -c fp -l no-pager -d 'Do not use pager'
complete -c fp -l pager -d 'Use specified pager' -r

`)

	// Find root command to get top-level subcommands
	var rootSubcmds []string
	for _, cmd := range commands {
		if len(cmd.Path) == 1 && cmd.Name == "fp" {
			rootSubcmds = cmd.Subcommands
			break
		}
	}

	// Generate completions for top-level commands
	b.WriteString("# Top-level commands\n")
	for _, subcmdName := range rootSubcmds {
		// Find the command to get its summary
		for _, cmd := range commands {
			if len(cmd.Path) == 2 && cmd.Name == subcmdName {
				desc := escapeForFish(cmd.Summary)
				b.WriteString(fmt.Sprintf("complete -c fp -n '__fish_use_subcommand' -a %s -d '%s'\n", subcmdName, desc))
				break
			}
		}
	}

	b.WriteString("\n")

	// Generate completions for subcommands
	for _, cmd := range commands {
		if len(cmd.Path) != 2 {
			continue
		}

		cmdName := cmd.Name

		// Add subcommands
		if len(cmd.Subcommands) > 0 {
			b.WriteString(fmt.Sprintf("# %s subcommands\n", cmdName))
			for _, subcmdName := range cmd.Subcommands {
				// Find subcommand summary
				subcmdPath := append(cmd.Path, subcmdName)
				if subcmd := FindCommand(commands, subcmdPath); subcmd != nil {
					desc := escapeForFish(subcmd.Summary)
					b.WriteString(fmt.Sprintf("complete -c fp -n '__fish_seen_subcommand_from %s; and not __fish_seen_subcommand_from %s' -a %s -d '%s'\n",
						cmdName, strings.Join(cmd.Subcommands, " "), subcmdName, desc))
				}
			}
		}

		// Add flags for the command
		if len(cmd.Flags) > 0 {
			b.WriteString(fmt.Sprintf("# %s flags\n", cmdName))
			for _, f := range cmd.Flags {
				writeFishFlag(&b, cmdName, f)
			}
		}

		if len(cmd.Subcommands) > 0 || len(cmd.Flags) > 0 {
			b.WriteString("\n")
		}
	}

	return b.String()
}

func writeFishFlag(b *strings.Builder, cmdName string, f FlagInfo) {
	if len(f.Names) == 0 {
		return
	}

	desc := escapeForFish(f.Description)

	var short, long string
	for _, name := range f.Names {
		if strings.HasPrefix(name, "--") {
			long = strings.TrimPrefix(name, "--")
		} else if strings.HasPrefix(name, "-") {
			short = strings.TrimPrefix(name, "-")
		}
	}

	parts := []string{"complete -c fp"}
	parts = append(parts, fmt.Sprintf("-n '__fish_seen_subcommand_from %s'", cmdName))

	if short != "" {
		parts = append(parts, fmt.Sprintf("-s %s", short))
	}
	if long != "" {
		parts = append(parts, fmt.Sprintf("-l %s", long))
	}
	if f.HasValue {
		parts = append(parts, "-r")
	}
	parts = append(parts, fmt.Sprintf("-d '%s'", desc))

	b.WriteString(strings.Join(parts, " ") + "\n")
}

func escapeForFish(s string) string {
	s = strings.ReplaceAll(s, "'", "\\'")
	s = strings.ReplaceAll(s, "\\", "\\\\")
	return s
}
